import { Button, Card, CardContent, CardMedia, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";

function ShoppingCart() {
  const [data, setData] = useState([]);
  let movieAPI = `https://api.pexels.com/v1/curated`;
  const API_KEY = "IRFfLTybS2GdykQ5nnk5Hm5HsFlnV4LfdPZ2hCGaURvB0HhTpKIprJ13";
  let movieData = async () => {
    try {
      let response = await axios
        .get(movieAPI, {
          headers: {
            Authorization: API_KEY,
          },
        })
        .then((res) => res.data.photos)
        .then((e) => setData(e));
    } catch (err) {
      console.error(err, "err");
    }
  };
  useEffect(() => {
    movieData();
    console.log(data);
  }, []);

  return (
    <div>
      <h2>Shopping</h2>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <div style={{ width: "70%" }}>
          <div className="row">
            {data
              ? data.map((e) => {
                  return (
                    <div
                      className="col-lg-4 col-md-6 col-sm-12 mb-3"
                      key={e.id}
                    >
                      <Card key={e.id} sx={{ margin: "1rem" }}>
                        <CardMedia
                          sx={{ height: 100 }}
                          image={e.src.original}
                          title={e.photographer}
                        />
                        <CardContent>
                          <Typography>{e.photographer}</Typography>
                          <div style={{display:'flex', justifyContent:'center', margin:'1rem 0 0 0'}}><Button variant="contained">Add to cart</Button></div>
                        </CardContent>
                      </Card>
                    </div>
                  );
                })
              : "Loading"}
          </div>
        </div>

        <div style={{ width: "30%" }}>
          <Card>
            <CardContent>
              <h3>Checkout</h3>
              <h3>Item name</h3>
              <h5>Quantity</h5>
              <h5>Price</h5>
              <h5>Total</h5>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ShoppingCart;
