import { Card, CardContent, CardMedia } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";

function ShoppingCart() {
  const [data, setData] = useState([]);
  let movieAPI = `https://api.pexels.com/v1/curated`;
  const API_KEY = "IRFfLTybS2GdykQ5nnk5Hm5HsFlnV4LfdPZ2hCGaURvB0HhTpKIprJ13";
  let movieData = async () => {
    try {
      let response = await axios
        .get(movieAPI, {
          headers: {
            Authorization: API_KEY,
          },
        })
        .then((res) => res.data.photos)
        .then((e) => setData(e));
    } catch (err) {
      console.error(err, "err");
    }
  };
  useEffect(() => {
    movieData();
    console.log(data);
  }, []);

  return (
    <div>
      <h2>Shopping</h2>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <div style={{ width: "70%" }}>
          <div className="row" >
            <div className="col-lg-3 col-md-4 col-sm-6">
              {data
                ? data.map((e) => {
                    return (
                      <card key={e.id} sx={{width:'30%'}}>
                     <img src={e.src.original} style={{width:'15rem'}}/>
                     <div class="card-body">{e.photographer}</div>
                        
                        
                      
                      </card>
                    );
                  })
                : "Loading"}
            </div>
          </div>
        </div>

        <div style={{ width: "30%" }}>
          <Card>
            <CardContent>
              <h3>Checkout</h3>
              <h3>Item name</h3>
              <h5>Quantity</h5>
              <h5>Price</h5>
              <h5>Total</h5>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ShoppingCart;
