import { Card, CardContent, CardMedia } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";

function ShoppingCart() {
  const [data, setData] = useState([]);
  let movieAPI = `https://pokeapi.co/api/v2/pokemon/?offset=20&limit=20`;
  let movieData = async () => {
    try {
      let response = await axios.get(movieAPI).then((j)=>j.data).then((e)=>setData([e.results] ));
   
    } catch (err) {
      console.error(err, "err");
    }
  };
  useEffect(() => {
    movieData();
    console.log(data);
  }, []);

  return (
    <div>
      <h2>Shopping</h2>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <div style={{ width: "70%" }}>
          <div className="row">
            <div className="col-lg-3 col-md-4 col-sm-6">
              {data
                ? data.map((e) => {
                    return (
                      <Card key={e.name}>
                        <CardMedia
                          sx={{ height: 100 }}
                          image={e.url}
                          title={e.name}
                        />
                        <CardContent>{e.name}</CardContent>
                    
                      </Card>
                      
                    );
                  })
                : "Loading"}
            </div>
          </div>
        </div>

        <div style={{ width: "30%" }}>
          <Card>
            <CardContent>
              <h3>Checkout</h3>
              <h3>Item name</h3>
              <h5>Quantity</h5>
              <h5>Price</h5>
              <h5>Total</h5>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ShoppingCart;
