import React, { useState } from "react";
import "./Cart.css";
import Box from "@mui/material/Box";
import {
  Button,
  Card,
  CardContent,
  TextField,
  Typography,
} from "@mui/material";

function Cart() {
  const [task, setTask] = useState(["swimming", "cycling"]);
  const [newTask, setNewTask] = useState("");

  let addTask = () => {
    if (newTask.trim() !== "") {
      setTask([...task, newTask]);
      setNewTask("");
    }
  };

  return (
    <div>
      <h1>Cart</h1>
      <h3>TO DO LIST</h3>
      <Box
        sx={{ display: "flex", width: "100%", justifyContent: "space-between" }}
      >
        <Card className="card" sx={{ width: "30%", backgroundColor: "azure" }}>
          <CardContent>
            <Typography sx={{ fontWeight: "700" }}>Task pending</Typography>
            {task.map((e) => {
              return (
                <div style={{display:'flex', alignItems:'center'}}>
                  <Typography>{e}</Typography>
                  <Button size='smalll'>Completed</Button>
                </div>
              );
            })}
          </CardContent>
        </Card>
        <Card className="card" sx={{ width: "30%", backgroundColor: "green" }}>
          <CardContent>
            <Typography sx={{ fontWeight: "700" }}>Task Completed</Typography>
          </CardContent>
        </Card>
        <Card className="card" sx={{ width: "30%" }}>
          <CardContent>
            <Typography sx={{ fontWeight: "700" }}>Add Task</Typography>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <TextField
                placeholder="Add Task"
                size="small"
                onChange={(e) => setNewTask(e.target.value)}
              ></TextField>
              <Button variant="outlined" onClick={addTask}>
                Submit
              </Button>
            </div>
          </CardContent>
        </Card>
      </Box>
    </div>
  );
}

export default Cart;
