import { Card, CardContent, CardMedia } from "@mui/material";
import React, { useEffect, useState } from "react";
import axios from "axios";

function ShoppingCart() {
  const [data, setData] = useState("");
  let movieAPI = `https://api.themoviedb.org/3/movie/popular?api_key=2a7983081d8b62e8df0ee3efbae0aa82`;
  let movieData = async () => {
    try {
      let response = await axios.get(movieAPI);
      if (response.status === 200) {
        setData(response.data.results);
      }
    } catch (err) {
      console.error(err, "err");
    }
  };
  useEffect(() => {
    movieData();
    console.log(data);
  }, []);

  return (
    <div>
      <h2>Shopping</h2>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <div style={{ width: "70%" }}>
          <div className="row">
            <div className="col-lg-3 col-md-4 col-sm-6">
              {data
                ? data.map((e) => {
                    return (
                      <Card>
                        <CardMedia
                          sx={{ height: 140 }}
                          image={e.backdrop_path}
                          title={e.title}
                        />
                        <CardContent>{e.title}</CardContent>
                      </Card>
                    );
                  })
                : "Loading"}
            </div>
          </div>
        </div>

        <div style={{ width: "30%" }}>
          <Card>
            <CardContent>
              <h3>Checkout</h3>
              <h3>Item name</h3>
              <h5>Quantity</h5>
              <h5>Price</h5>
              <h5>Total</h5>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

export default ShoppingCart;
